import React, { useState } from 'react'
import './Card.css'
import Button from '../Button'

function Popup(props) {
    return (props.trigger) ? (
        <div className='popup'>
            <div className='popup-inner'>
                <button className='close-btn' onClick={()=>props.setTrigger(false)}>Close</button>
                {props.children}
            </div>
        </div>
    ) : "";
}

const Card = ({header, body, imgLink, displayCode, codeLink}) => {
    const [buttonPopup, setButtonPopup] = useState(false)
    return (
        <>
            <Popup trigger={buttonPopup} setTrigger={setButtonPopup}></Popup>
            <div className = "card">
                <div className = "cardImage"><img src = {imgLink}></img></div>
                <div className = "cardContainer">
                    <h1 className = "cardHeader">{header} </h1>
                    <h4 className = "cardBody">{body}</h4>
                    <br></br>
                    <h3 className='cardDate'>{date}</h3>
                    <button className = "codeBtn" onClick={()=>setButtonPopup(true)}><a href= {displayCode} >View Code</a></button>
                    <button className = "projectBtn"><a href= {codeLink}>View Project</a></button>  
                </div>
            </div>

        </>
    )
}

export default Card
