import React from 'react'
import './Home.css'
import ReactCardFlip from 'react-card-flip'


const Home = () => {
	// constructor () {
	// 	super();
	// 		this.state = { isFlipped: false }
	// 	this.handleClick = this.handleClick.bind(this);
	// }

	return (
		<>
			<div className='homeContainer' style={{ backgroundImage: `url("./Personal-Site/Images/Home/background3.png")`, backgroundAttachment:'fixed', backgroundSize:'100% 100%' }}>
				<img className='homeImg' src='./Personal-Site/Images/Home/me2.jpg'></img>
				<img className='homeImg' src='./Personal-Site/Images/Home/cheers.jpg'></img>
				<img className='homeImg' src='./Personal-Site/Images/Home/me1.jpg'></img>
				<img className='homeImg' src='./Personal-Site/Images/Home/boats.jpg'></img>
			</div>
		</>
	)
}


class App extends React.Component {
  constructor() {
    super();
      this.state = {
      isFlipped: false
    };
    this.handleClick = this.handleClick.bind(this);
  }

  handleClick(e) {
    e.preventDefault();
    this.setState(prevState => ({ isFlipped: !prevState.isFlipped }));
  }

  render() {
    return (
      <ReactCardFlip isFlipped={this.state.isFlipped} flipDirection="vertical">
        <YOUR_FRONT_CCOMPONENT>
          This is the front of the card.
          <button onClick={this.handleClick}>Click to flip</button>
        </YOUR_FRONT_CCOMPONENT>

        <YOUR_BACK_COMPONENT>
          This is the back of the card.
          <button onClick={this.handleClick}>Click to flip</button>
        </YOUR_BACK_COMPONENT>
      </ReactCardFlip>
    )
  }
}


export default App
